// eslint-disable-next-line
import React,{useState} from "react";
import  { Navigate }  from "react-router-dom"
import axios from "axios";
import "./pageSignup.css"
function Signup() {

const [user,setUser]=useState({
  name:"",
  email:"",
  password:""
})

function handleChange(event){
  const newuser={...user}
  newuser[event.target.id]=event.target.value
  setUser(newuser)
 // console.log(newuser);
}

/*function handleClick(event){
  event.preventDefault();
  axios.post("https://clubs-united.herokuapp.com/register",user)
  .then(res=>{
    //console.log(res)
    alert("Query Submitted "+user.name);
  })
  
}*/

return (
    <div>
        <section className="box">
      
      <h1 className="text-center">Sign Up</h1>
      <div className="form1 fluid-container">
        <div className="row">
          <div className="col-md-6 col-10 mx-auto">
            <form>
              <div className="form-group">
              <label>Enter your Full Name</label>
                <input
                  type="text"
                  id="name"
                  value={user.name}
                  className="form-control"
                  placeholder="Full Name"
                  name="name"
                  onChange={handleChange}
                />
                <label>Enter your Email id</label>
                <input
                  type="email"
                  id="email"
                  value={user.email}
                  className="form-control"
                  placeholder="Email id"
                  name="email"
                  onChange={handleChange}
                />
                <label for="exampleInputEmail1">Enter your Password</label>
                <input
                  type="password"
                  className="form-control"
                  id="password"
                  name="password"
                  value={user.password}
                  placeholder="Password"
                  onChange={handleChange}
                />
                <label for="exampleInputEmail1">Re-enter your Password</label>
                <input
                  type="password"
                  className="form-control"
                  id="password"
                  name="password"
                  value={user.password}
                  placeholder="Password"
                  onChange={handleChange}
                />
              </div>
              <button type="submit" className="btn btn-primary last">
                Submit
              </button>
            </form>
            
          </div>
          
        </div>
        <p>Already a member? <a href="/login">Log in</a></p>
      </div>
      
      </section>
      <footer className="foot text-center">
          <p>Â© Copyright 2022 Clubs United</p>
          <p>
            Made with <i className="fa-solid fa-heart"></i> by Gaurav Ahuja
          </p>
        </footer>
    </div>
  );              
}
export default Signup;
